package v2

import (
	"bytes"
	"encoding/base64"
	"testing"

	"github.com/ProtonMail/go-crypto/openpgp/packet"
)

func TestUserattribute_CompatibleGPG2_3_6(t *testing.T) {
	_, err := ReadArmoredKeyRing(bytes.NewBufferString(pgpPhotoPublicGPG2_3_6))
	if err != nil {
		t.Fatal(err)
	}
}

func TestAddPhoto(t *testing.T) {
	entity, err := NewEntity("Golang Gopher", "Test Key", "no-reply@golang.com", nil)
	if err != nil {
		t.Fatal(err)
	}

	jpegBase64 := ""
	jpegBytes, err := base64.RawStdEncoding.DecodeString(jpegBase64)
	if err != nil {
		t.Fatal(err)
	}

	err = entity.AddPhotos([][]byte{jpegBytes}, nil)
	if err != nil {
		t.Fatal(err)
	}

	serializedEntity := bytes.NewBuffer(nil)
	entity.SerializePrivate(serializedEntity, nil)

	entity2, err := ReadEntity(packet.NewReader(bytes.NewBuffer(serializedEntity.Bytes())))
	if err != nil {
		t.Fatal(err)
	}

	if len(entity2.Attributes) != 1 {
		t.Fatal("data err, entity have no uat")
	}

	imgBytes := entity2.Attributes[0].UserAttribute.ImageData()
	if !bytes.Equal(imgBytes[0], jpegBytes) {
		t.Fatal("image data not equal")
	}

}
